mapper.select:
select s.store_name, s.image_link
from Product as p
inner Join Menu as m on p.pro_seq = m.pro_seq
inner join Store as s on m.store_seq = s.store_seq
where p.name like #{keyword}
order by m.price desc;

select s.store_name, s.image_link
from Product as p
inner Join Menu as m on p.pro_seq = m.pro_seq
inner join Store as s on m.store_seq = s.store_seq
where p.name like #{keyword}
order by m.price asc;
